package lius.index.rtf;/* * Licensed to the Apache Software Foundation (ASF) under one or more * contributor license agreements.  See the NOTICE file distributed with * this work for additional information regarding copyright ownership. * The ASF licenses this file to You under the Apache License, Version 2.0 * (the "License"); you may not use this file except in compliance with * the License.  You may obtain a copy of the License at * *      http://www.apache.org/licenses/LICENSE-2.0 * * Unless required by applicable law or agreed to in writing, software * distributed under the License is distributed on an "AS IS" BASIS, * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied. * See the License for the specific language governing permissions and * limitations under the License. */import java.io.IOException;import java.util.ArrayList;import java.util.Collection;import java.util.Iterator;import javax.swing.text.BadLocationException;import javax.swing.text.DefaultStyledDocument;import javax.swing.text.rtf.RTFEditorKit;import lius.config.LiusConfig;import lius.config.LiusField;import lius.index.BaseIndexer;import lius.index.ParsingResult;import org.apache.log4j.Logger;import org.springframework.core.io.Resource;/** * Classe permettant d'indexer des fichiers RTF. <br/><br/> Class that indexes * RTF files. *  * @author Rida Benjelloun (ridabenjelloun@gmail.com) */public class RTFIndexer extends BaseIndexer {    static Logger logger = Logger.getLogger(RTFIndexer.class);    @Override    public int getType() {        return 1;    }    @Override    public boolean isConfigured(LiusConfig liusConfig) {        boolean ef = false;        if (liusConfig.getRtfFields() != null)            return ef = true;        return ef;    }    @Override    public Collection getConfigurationFields(LiusConfig liusConfig) {        return liusConfig.getRtfFields();    }    public String getContent(Resource resource) {        String content = "";        try {            DefaultStyledDocument sd = new DefaultStyledDocument();            RTFEditorKit kit = new RTFEditorKit();            kit.read(resource.getInputStream(), sd, 0);            content = sd.getText(0, sd.getLength());        } catch (IOException e) {            throw new RuntimeException(e);        } catch (BadLocationException j) {            throw new RuntimeException(j);        }        return content;    }    /**     * Retourne une collection contenant les champs avec les valeurs à indexer     * comme par exemple : le texte integral, titre etc. <br/><br/> Returns a     * collection containing the fields with the values to index like : full     * text, title, etc.     */    @Override    public ParsingResult parseResource(LiusConfig liusConfig,            Resource resource) {        Collection coll = new ArrayList();        String text = getContent(resource);        Iterator it = liusConfig.getRtfFields().iterator();        while (it.hasNext()) {            Object field = it.next();            if (field instanceof LiusField) {                LiusField lf = (LiusField) field;                if (lf.getGet() != null) {                    if (lf.getGet().equalsIgnoreCase("content")) {                        lf.setValue(text);                        coll.add(lf);                    }                }            } else {                coll.add(field);            }        }        return new ParsingResult(coll, text);    }}