package lius.test.beans;/** * Example of bean indexing *  * @author Rida Benjelloun (ridabenjelloun@gmail.com) */public class Personne {    private String nom;    private String prenom;    private String adresse;    public void setNom(String nom) {        this.nom = nom;    }    public String getNom() {        return nom;    }    public void setPrenom(String prenom) {        this.prenom = prenom;    }    public String getPrenom() {        return prenom;    }    public void setAdresse(String adresse) {        this.adresse = adresse;    }    public String getAdresse() {        return adresse;    }}