package lius.index.javaobject;/* * Licensed to the Apache Software Foundation (ASF) under one or more * contributor license agreements.  See the NOTICE file distributed with * this work for additional information regarding copyright ownership. * The ASF licenses this file to You under the Apache License, Version 2.0 * (the "License"); you may not use this file except in compliance with * the License.  You may obtain a copy of the License at * *      http://www.apache.org/licenses/LICENSE-2.0 * * Unless required by applicable law or agreed to in writing, software * distributed under the License is distributed on an "AS IS" BASIS, * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied. * See the License for the specific language governing permissions and * limitations under the License. */import java.lang.reflect.InvocationTargetException;import java.lang.reflect.Method;import java.util.ArrayList;import java.util.Collection;import java.util.Iterator;import java.util.List;import lius.config.LiusConfig;import lius.config.LiusField;import lius.index.BaseIndexer;import lius.index.ParsingResult;import lius.index.util.LiusUtils;import org.apache.log4j.Logger;/** * Classe permettant d'indexer des objets JavaBeans <br/><br/> Class for * indexing JavaBeans objects. *  * @author Rida Benjelloun (ridabenjelloun@gmail.com) */public class BeanIndexer extends BaseIndexer {    static Logger logger = Logger.getLogger(BeanIndexer.class);    @Override    public int getType() {        return 2;    }    @Override    public boolean isConfigured(LiusConfig liusConfig) {        boolean ef = false;        if (liusConfig.getJavaBeansFields() != null)            return ef = true;        return ef;    }    @Override    public Collection getConfigurationFields(LiusConfig liusConfig) {        List ls = new ArrayList();        ls.add(liusConfig.getJavaBeansFields());        return ls;    }    @Override    public ParsingResult parseObject(LiusConfig liusConfig, Object object) {        Collection collRes = new ArrayList();        try {            Class c = object.getClass();            Collection liusFields = (Collection) liusConfig                    .getJavaBeansFields().get(c.getName());            Iterator i = liusFields.iterator();            while (i.hasNext()) {                Object field = i.next();                if (field instanceof LiusField) {                    LiusField lf = (LiusField) field;                    Method m = c.getMethod(lf.getGetMethod());                    Object o = m.invoke(object);                    String value = o.toString();                    lf.setValue(value);                    collRes.add(lf);                } else {                    collRes.add(field);                }            }        } catch (NoSuchMethodException e) {            throw new RuntimeException(e);        } catch (IllegalAccessException e) {            throw new RuntimeException(e);        } catch (InvocationTargetException e) {            throw new RuntimeException(e);        }        return new ParsingResult(collRes, getContent(object));    }    public String getContent(Object object) {        StringBuffer sb = new StringBuffer();        try {            Class c = object.getClass();            Collection liusFields = (Collection) liusConfig                    .getJavaBeansFields().get(c.getName());            Iterator i = liusFields.iterator();            while (i.hasNext()) {                Object field = i.next();                if (field instanceof LiusField) {                    LiusField lf = (LiusField) field;                    Method m = c.getMethod(lf.getGetMethod());                    Object o = m.invoke(object);                    sb.append(o.toString() + " ");                }            }        } catch (NoSuchMethodException e) {            throw new RuntimeException(e);        } catch (IllegalAccessException e) {            throw new RuntimeException(e);        } catch (InvocationTargetException e) {            throw new RuntimeException(e);        }        return sb.toString();    }}